  it must "build a single letter" in {
    assertResult(
      "### \n" +
      "#   \n" +
      "##  \n" +
      "#   \n" +
      "### "
    )(AsciiArt(standard, 4, "E"))
  }

  it must "repeat letters" in {
    assertResult(
      "# #  #  ### # #  #  ### ###  #  ### \n" +
      "### # # # # # # # #  #   #  # # # # \n" +
      "### ### # # ### ###  #   #  ### # # \n" +
      "# # # # # # # # # #  #   #  # # # # \n" +
      "# # # # # # # # # #  #   #  # # # # "
    )(AsciiArt(standard, 4, "MANHATTAN"))
  }

  it must "ignore case" in {
    assertResult(
      "# #  #  ### # #  #  ### ###  #  ### \n" +
      "### # # # # # # # #  #   #  # # # # \n" +
      "### ### # # ### ###  #   #  ### # # \n" +
      "# # # # # # # # # #  #   #  # # # # \n" +
      "# # # # # # # # # #  #   #  # # # # "
    )(AsciiArt(standard, 4, "ManhAtTan"))
  }

  it must "use '?' for non letter characters" in {
    assertResult(
      "# # ### ### # # ### ### ### ### ### \n" +
      "###   # # # # #   #  #   #    # # # \n" +
      "###  ## # # ###  ##  #   #   ## # # \n" +
